mapper.sql:
SELECT *
FROM product


        product_id = #{keyword}



        name LIKE CONCAT('%', CONCAT(#{keyword}, '%'))



        price BETWEEN #{minPrice} AND #{maxPrice}



        category = #{keywordCategory}



        status = #{keywordStatus}

mapper.update:
UPDATE product
SET name = #{name}, price = #{price}, category = #{category}, status = #{status}
WHERE product_id = #{productId}
